import React from "react";

export const Book = ({ count, data: { id, isRead, title }, onRemoveBook, onSetReadBook }) => {
    const titleStyle = `book-title ${isRead ? "isRead" : ""}`;
    return (

        <li className="book-wrapper">
            <span className="book-count">{count}</span>
            <span className="book-title">{title}</span>
            <span className="row">
                <span className="pointer" onClick={() => onRemoveBook(id)}>## </span>
                <input
                    type="checkbox"
                    className="pointer"
                    checked={isRead}
                    onChange={() => onSetReadBook()}
                />
            </span>
        </li>
    )
}
