import React, { useState } from 'react';
import BOOK_LIST from './store/books.json'
import { Books } from './components/Books'
//({ count, data: { id, isRead, title }, onRemoveBook, onSetReadBook })

const App = () => {

  const [bookList, setBookList] = useState(BOOK_LIST);

  const removeBook = (id) => {
    setBookList((prev) => prev.filter((book) => book.id !== id));
  }
  const onReadBook = (id) => {

    const idx = bookList.findIndexOf((item) => item.id === id);
    const oldObj = bookList[idx];
    const newObj = { ...oldObj, isRead: !oldObj.isRead };
    setBookList(prev => ([...prev.slice(0, idx), newObj, ...prev.slice(idx + 1)]))

  }

  return (
    <div className="container">

      <Books data={bookList} onRemoveBook={removeBook} onReadBook={onReadBook} />

    </div>
  );
};

export default App;
